  for i in range(len(dramas)):
    print(i+1,dramas[i])

  print("="*50)

  num = input("Choose the number of drama that you like to know about:")
  if num.isdigit() and  int(num)>=1 and int(num)<=15:
    num = int(num)
    print("\n"+dramas[num-1]+"\n"+descriptions[num-1])
  else:
    print("\nWrong input. The number should be between 1 and 15")
  
  ch = input("\nDo you like to quit? y/n:")
  if ch.lower() == 'y':
    str = "GoodBye"
    for i in range(len(str)):
        print(" "*(len(str)-i),"*"*i,str[i],"*"*i," "*(len(str)-i))
    break
